insert into wp_erpress2_tracks(episode_id, artist_id, album_id, title, position) select t.id_episode, a.id as artist_id, b.id as album_id, t.titre, t.position from wp_erpress_titres t left join wp_erpress2_artists a on a.name = t.artiste left join wp_erpress2_albums b on b.title = t.album and b.artist_id = a.id where a.id is not null and b.id is not null order by artiste, album;
insert into wp_erpress2_tracks(episode_id, artist_id, album_id, title, position) select t.id_episode, a.id as artist_id, b.id as album_id, t.titre, t.position from wp_erpress_titres t left join wp_erpress2_artists a on a.name = 'Cherri Bomb' left join wp_erpress2_albums b on b.title = 'This Is The End Of Control' where t.artiste = 'Cheri Bomb' order by artiste, album;
insert into wp_erpress2_tracks(episode_id, artist_id, album_id, title, position) select t.id_episode, a.id as artist_id, b.id as album_id, t.titre, t.position from wp_erpress_titres t left join wp_erpress2_artists a on a.name = t.artiste left join wp_erpress2_albums b on b.title = 'Losing Faith' and b.artist_id = a.id where t.album = 'Loosing Faith' order by artiste, album;
